// DATASET: kitties from ./datasets/kitties
const kittyPrompts = {
  orangeKittyNames() {
    // Return an array of just the names of kitties who are orange e.g.
    // ['Tiger', 'Snickers']

    const result = kitties.filter((cat) => { return cat.color === "orange"}).map((name) => { return name.name});
    return result;

    // Annotation:
    // first i need to filter out the array to get only the cats with orange
    // then i map the array to just get the value of the name from each object of the filter array
  },

  sortByAge() {
    // Sort the kitties by their age

    const result = kitties.sort((catA, catB) => {
        return catB.age - catA.age; 
    });
    return result;

    // Annotation:
    // sorted the cat from oldest to youngest using the sort prototype
  },

  growUp() {
    // Return an array of kitties who have all grown up by 2 years e.g.
    // [{
    //   name: 'Felicia',
    //   age: 4,
    //   color: 'grey'
    // },
    // {
    //   name: 'Tiger',
    //   age: 7,
    //   color: 'orange'
    // },
    // ...etc]

    const result = kitties.map((cat) => {
        return {name: cat.name, age: (cat.age + 2), color: cat.color}
    })
    return result;
  }

    // Annotation:
    // i map it because the array is still the same length
    // created a instance of a object inside the arrow function to push out a new object with the new age
};






