// DATASET: clubs from ./datasets/clubs
const clubPrompts = {
  membersBelongingToClubs() {
    // Create an object whose keys are the names of people, and whose values are
    // arrays that include the names of the clubs that person is a part of. e.g. 
    // {
    //   Louisa: ['Drama', 'Art'],
    //   Pam: ['Drama', 'Art', 'Chess'],
    //   ...etc
    // }

    const result = clubs.reduce((acc, club) => {
           club.members.forEach((member) => {
                if(!acc[member]) {
                    acc[member] = [];
                }
                acc[member].push(club.club)
           })
        return acc
    }, {});
    return result;

    // Annotation:
    // i use reduce because i need the array to be an object
    // use an foreach() loop within the arrow function of the reduce prototype
    // in the foreach its checking if the name is a key i not to create one with an array
    // after the if itll push the club into the array to where it belongs
  }
};






